  Intercept.t = round(fixef.herb[1,3], 3),
  Intercept.p = round(fixef.herb[1,4], 3),
  Time.Estimate = round(fixef.herb[2,1], 3),
  Time.SE = round(fixef.herb[2,2], 3),
  Time.t = round(fixef.herb[2,3], 3),
  Time.p = round(fixef.herb[2,4], 3),
